sp_widget.client_script:
function($scope,$rootScope,$timeout) {

  /* widget controller */

  var c = this;

  $scope.showWidget = "";

        //Listening for "showHideWidget" event

  $rootScope.$on('showHideWidget', function(event,data) {

  $timeout(function(){

            $scope.showWidget = data;

  });

  });

}

sp_widget.css:
.each-incident{

